<?php defined('BASEPATH') OR exit('No direct script access allowed');class Categorias extends CI_Controller {        public function __construct() {            parent::__construct();            $this->load->model("categorias_model", "Mcat");            $this->categorias = $this->Mcat->lista_categorias();        }	public function index($id, $slug=null){ /*'slug' é a segunda variavel, por que tem <url-do-site>id/nome-da-categoria (tem que fazer isso pra pegar a primeira apenas) */                $dados['categorias'] = $this->categorias;                $this->load->model("noticias_model", "Mnoticias"); /*Mnoticias é o alias do model que acabei de chamar*/                $dados['noticias_filtradas'] = $this->Mnoticias->noticias_por_categoria($id);                $dados['titulo_categoria'] = $this->Mcat->lista_titulo($id);                $dados['title'] = "Categorias - ";		$this->load->view('frontend/template/html-header', $dados);		$this->load->view('frontend/template/header');		$this->load->view('frontend/categoria');		$this->load->view('frontend/template/footer');		$this->load->view('frontend/template/html-footer');	}}