<?phpob_start();defined('BASEPATH') OR exit('No direct script access allowed');class Chales_Controller extends CI_Controller {    public function __construct() {        parent::__construct();        $this->load->model("chale_model", "mcha");        $this->load->model("tarifa_model", "mtar");        $this->load->model("fotosChale_model", "mfot");    }    public function index() {        $dados = dados(array(            "title" => "ChalÃ©s - Detalhes de Minas",            "chales" => $this->mcha->lista_chales()        ));        $this->load->view('frontend/template/html-header', $dados);        $this->load->view('frontend/template/header');        $this->load->view('frontend/chales');        $this->load->view('frontend/template/footer');        $this->load->view('frontend/template/html-footer');    }    public function ver_chale() {        $this->load->helper('url');        $quarto = $this->uri->segment(2);        $chale = $this->mcha->lista_chale($quarto);        $dados = dados(array(            "title"   => $chale[0]['cha_nome'] . " - Detalhes de Minas",            "chale"   => $chale,            "chales"    => $this->mcha->lista_chales(),            "fotos"   => $this->mfot->lista_fotos($quarto),            "tarifas" => $this->mtar->lista_tarifas($quarto)        ));        call_view("chale", $dados);    }}