<?php ob_start(); defined('BASEPATH') OR exit('No direct script access allowed');class Home_Controller extends CI_Controller {    public function __construct() {        parent::__construct();    }    public function index() {        $this->load->model("slide_model", "msli");        $this->load->model("chale_model", "mcha");                $dados = dados(array(            "title" => "Detalhes de Minas",            "slide" => $this->msli->lista_slides(),            "chales" => $this->mcha->lista_chales()            ));         call_view("home", $dados);            }}