<?php ob_start();defined('BASEPATH') OR exit('No direct script access allowed');class Sobre_Controller extends CI_Controller {    public function __construct() {        parent::__construct();         $this->load->model("textos_model", "mtxt");        $this->load->model("imagem_model", "mimg");        $this->load->model("galeria_model", "mgal");        $this->load->model("categorias_model", "mcat");        $this->load->helper("do_upload");    }     public function index() {                foreach ($this->mtxt->lista_textos_geral() as $texto) {            $txt_id = $texto['txt_id'];            $txt[$txt_id] = $texto['txt_texto'];        }        foreach ($this->mimg->lista_imagem_geral() as $imagem) {            $img_id = $imagem['img_id'];            $img[$img_id] = $imagem['img_imagem'];            $img_alt[$img_id] = $imagem['img_alt'];            $img_title[$img_id] = $imagem['img_title'];        }                $dados['galeria'] = $this->mgal->lista_galerias();        $dados['categorias'] = $this->mcat->lista_categorias();                $dados["img"] = $img;        $dados["img_alt"] = $img_alt;        $dados["img_title"] = $img_title;        $dados["txt"] = $txt;        $dados['title'] = "Sobre NÃ³s - Detalhes de Minas";        $this->load->view('frontend/template/html-header', $dados);        $this->load->view('frontend/template/header');        $this->load->view('frontend/sobre');        $this->load->view('frontend/template/footer');        $this->load->view('frontend/template/html-footer');    }}